package oops;

public class vowels {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	  char st = 's';
	  if (st == 'a' || st == 'e' || st == 'i' || st == 'o' || st == 'u' || st == 'A' || st == 'E' || st == 'O' ||
			  st == 'U') {
			  System.out.println(st +" is a vowel");
	  }
	  else
	  {
		  System.out.println(st + " is consonants");
	  }
	  
	}

}
